import React from "react";
import { CardDataService } from "../modules/CardDataService";

const Card: React.FC<CardDataService> = ({
  title,
  text,
  imgSrc,
  m_text,
  fields,
  id,
}) => {
  const modalId = `exampleModal${id}`;
  const animationStyle = {
    animationDelay: `${id * 0.1}s`  // Adjust the multiplier to control the delay between animations
  };

  return (
    <div className="card bg-warning text-light card-rise" style={{ width: "22rem", ...animationStyle }}>
      <img src={imgSrc} className="card-img-top" alt={title} />
      <div className="card-body">
        <h5 className="card-title text_black">{title}</h5>
        <p className="card-text">{text}</p>
        <button
          type="button"
          className="btn btn-dark"
          data-bs-toggle="modal"
          data-bs-target={`#${modalId}`}
        >
          Get Started
        </button>
      </div>
      {/* Modal For Migration Armenia START */}
      <div className="modal fade text-dark" id={modalId} tabIndex={-1} aria-labelledby={`${modalId}Label`} aria-hidden="true">
        <div className="modal-dialog modal-xl">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-3 text_black" id={`${modalId}Label`}>{title}</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body text-black">
              <span className="text-center fs-4">{m_text}</span>
              {Object.entries(fields).map(([key, value], index) => (
                value && value.length > 0 && (
                  <div className="text-center mt-4 p-2" key={key}>
                    <h4 className="bg-warning custom_title_a">{value[0]}</h4>
                    <ul className="list-unstyled custUl">
                      {value.map((item, idx) => idx > 0 && <li key={idx} className="custli">{item}</li>)}
                    </ul>
                    {fields.icon_aps && fields.icon_aps[index] && (
                      <img src={fields.icon_aps[index] || "/default.png"} alt="/" className="img_icon" />
                    )}
                  </div>
                )
              ))}
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-dark">Order a service</button>
            </div>
          </div>
        </div>
      </div>
      {/* Modal For Migration Armenia END */}
    </div>
  );
};

export default Card;
