import React from "react";
import { CardDataService } from "../modules/CardDataService";


const Card: React.FC<CardDataService> = ({
  title,
  text,
  imgSrc,
  m_text,
  fields,
  id,
}) => {
  const modalId = `exampleModal${id}`;
  const animationDelay = `${id * 0.1}s`; // Используйте ID для задержки или замените на index, если ID не подходит

  return (
    <div className="card bg-warning text-light card-rise" style={{ width: "22rem", animationDelay }}>
      <img src={imgSrc} className="card-img-top" alt={title} />
      <div className="card-body">
        <h5 className="card-title text_black">{title}</h5>
        <p className="card-text">{text}</p>
        <button
          type="button"
          className="btn btn-dark"
          data-bs-toggle="modal"
          data-bs-target={`#${modalId}`}
        >
          Get Started
        </button>
      </div>
      {/* Modal component remains the same */}
    </div>
  );
};

export default Card;
